-- Создание таблиц

CREATE TABLE category (
    id INT UNSIGNED NOT NULL AUTO_INCREMENT,
    name VARCHAR(255) NOT NULL,
    image VARCHAR(255) DEFAULT 'img/default__image.gif',
    CONSTRAINT pk_category PRIMARY KEY (id)
);

CREATE TABLE product (
    id INT UNSIGNED NOT NULL AUTO_INCREMENT,
    name VARCHAR(255) NOT NULL,
    image VARCHAR(255) NOT NULL DEFAULT 'img/default__image.gif',
    price DECIMAL(7,2) UNSIGNED NOT NULL,
    description TEXT,
    category_main_id INT UNSIGNED NOT NULL,
    CONSTRAINT pk_product PRIMARY KEY (id),
    CONSTRAINT fk_main_category_id FOREIGN KEY (category_main_id)
    REFERENCES category (id) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE product_category_is (
    product_id INT UNSIGNED NOT NULL,
    category_id INT UNSIGNED NOT NULL,
    CONSTRAINT fk_product_id FOREIGN KEY (product_id)
    REFERENCES product (id) ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT fk_category_id FOREIGN KEY (category_id)
    REFERENCES category (id) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE news (
    id INT UNSIGNED NOT NULL AUTO_INCREMENT,
    title VARCHAR(255) NOT NULL,
    `date` DATE NOT NULL,
    announcement TEXT,
    description TEXT,
    CONSTRAINT pk_news PRIMARY KEY (id)
);

CREATE TABLE feedback (
    id INT UNSIGNED NOT NULL AUTO_INCREMENT,
    name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL,
    phone VARCHAR(20) NOT NULL,
    description TEXT NOT NULL,
    CONSTRAINT pk_feedback PRIMARY KEY (id)
);

